import React, { useState } from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import './App.css';

const formSchema = Yup.object().shape({
  name: Yup.string().required('Name is required'),
  age: Yup.number().required('Age is required').positive().integer(),
  file: Yup.mixed().test("required", "You need to provide a file", (file) => {
    return file && file.length;
  }),
});

const fieldSchema = [
  {
    name: 'name',
    type: 'text',
    gridLayout: '1 / 2',
    validations: { required: true }
  },
  {
    name: 'age',
    type: 'number',
    gridLayout: '2 / 2',
    validations: { required: true }
  },
  {
    name: 'gender',
    type: 'radio',
    options: ['Male', 'Female'],
    gridLayout: '1 / 3',
    validations: { required: true }
  },
  {
    name: 'file',
    type: 'file',
    gridLayout: '2 / 3',
    validations: { required: true }
  }
];

const App = () => {
  const [step, setStep] = useState(0);
  const { register, handleSubmit, watch, formState: { errors }, control } = useForm({
    resolver: yupResolver(formSchema),
  });

  const onSubmit = data => {
    console.log(data);
    if (step < steps.length - 1) {
      setStep(step + 1);
    } else {
      console.log('Form submitted:', data);
    }
  };

  const steps = [
    [
      { name: 'name', label: 'Name', type: 'text', validation: { required: true } },
      { name: 'age', label: 'Age', type: 'number', validation: { required: true } },
    ],
    [
      { name: 'gender', label: 'Gender', type: 'radio', options: ['Male', 'Female'] },
      { name: 'file', label: 'Upload File', type: 'file' }
    ]
  ];

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="form-step">
          {steps[step].map((field, index) => (
            <div key={index} className="form-field" style={{ gridColumn: field.gridLayout }}>
              {field.type === 'text' || field.type === 'number' ? (
                <input
                  type={field.type}
                  {...register(field.name)}
                  placeholder={field.label}
                />
              ) : field.type === 'radio' ? (
                field.options.map((option, i) => (
                  <label key={i}>
                    <input type="radio" value={option} {...register(field.name)} />
                    {option}
                  </label>
                ))
              ) : field.type === 'file' ? (
                <input type="file" {...register(field.name)} />
              ) : null}
              {errors[field.name] && <span>{errors[field.name].message}</span>}
            </div>
          ))}
        </div>
        <button type="submit">{step < steps.length - 1 ? 'Next' : 'Submit'}</button>
        {step > 0 && <button type="button" onClick={() => setStep(step - 1)}>Back</button>}
      </form>
    </div>
  );
};

export default App;
