// src/App.js
import React from 'react';
import ReusableForm from './ReusableForm';
import * as Yup from 'yup';

function App() {
  const formFields = [
    {
      name: "firstName",
      type: "text",
      label: "First Name",
      gridLayout: "1fr 2fr",
      validations: Yup.string().required("First name is required"),
    },
    {
      name: "age",
      type: "number",
      label: "Age",
      gridLayout: "1fr 2fr",
      validations: Yup.number()
        .typeError("Age must be a number")
        .required("Age is required"),
    },
    {
      name: "gender",
      type: "select",
      label: "Gender",
      gridLayout: "1fr 2fr",
      options: [
        { value: "", label: "Select..." },
        { value: "male", label: "Male" },
        { value: "female", label: "Female" },
      ],
      validations: Yup.string().required("Gender is required"),
    },
  ];

  return (
    <div>
      <h1>Reusable Form Example</h1>
      <ReusableForm formFields={formFields} />
    </div>
  );
}

export default App;
