import React from 'react';
import { useForm, Controller } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';

// Define the validation schema
const schema = yup.object().shape({
  name: yup.string().required('Name is required'),
  age: yup.number().positive().integer().required('Age is required'),
  hobbies: yup.array().of(yup.string()).required('At least one hobby is required'),
  email: yup.string().email('Invalid email').required('Email is required'),
});

const App = () => {
  const { register, handleSubmit, control, formState: { errors } } = useForm({
    resolver: yupResolver(schema),
    defaultValues: {
      hobbies: [],
    },
  });

  const onSubmit = (data) => {
    // Ensure hobbies is an array
    console.log(data);
  };

  return (
    <div className="form-container">
      <h1>Reusable Form</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="form-field">
          <label>Name</label>
          <input type="text" {...register('name')} />
          {errors.name && <span className="error-message">{errors.name.message}</span>}
        </div>

        <div className="form-field">
          <label>Age</label>
          <input type="number" {...register('age')} />
          {errors.age && <span className="error-message">{errors.age.message}</span>}
        </div>

        <div className="form-field">
          <label>Email</label>
          <input type="email" {...register('email')} />
          {errors.email && <span className="error-message">{errors.email.message}</span>}
        </div>

        <div className="form-field">
          <label>Hobbies</label>
          <Controller
            name="hobbies"
            control={control}
            render={({ field }) => (
              <select multiple {...field} onChange={(e) => {
                const values = Array.from(e.target.selectedOptions, option => option.value);
                field.onChange(values);
              }}>
                <option value="traveling">Traveling</option>
                <option value="reading">Reading</option>
                <option value="cooking">Cooking</option>
                <option value="sports">Sports</option>
                <option value="music">Music</option>
              </select>
            )}
          />
          {errors.hobbies && <span className="error-message">{errors.hobbies.message}</span>}
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default App;
